import re


class Phrase:
    # Class to represent a phrase or phrases
    def __init__(self, content):
        self.content = content

    # Checks if string is equal to reversed string and returns true, false if not
    def isPalindrome(self):
        return self.processed_content() == reverse(self.processed_content())

    # Process Content for Palindrome Testing
    def processed_content(self):
        return self.letters().lower()

    # Return Letters for Phrase() content
    def letters(self):
        return "".join(re.findall(r"[a-zA-Z]", self.content))


# Reverses a string
def reverse(string):
    return "".join(reversed(string))
